mapper.update:
update item
set delete_flag = !delete_flag
where id = #{id}

mapper.delete:
delete
from u_e
where uid = #{uid}
  and eid = #{eid};

mapper.select:
select id, title, description, found_time, start_time, end_time, predict_start_time, predict_end_time, delete_flag, accomplish_status, complexity, share, u_id from item
title = #{title}
and description = #{describe}
and (DATEDIFF(DATE_ADD(#{date}, INTERVAL 1 DAY),predict_start_time) > 0 and DATEDIFF(predict_start_time, #{date}) >= 0)
and delete_flag = #{deleteFlag}
and accomplish_status = #{accomplishStatus}
and complexity = #{complexity}
and share = #{share}
and u_id = #{uid}

select *
from u_e


        eid =
        #{eid}


        and uid =
        #{uid}
