mapper.update:
update message
set status  = #{status},
    message = #{message}
where ru_id = #{ruID}
  and cu_id = #{cuID};

mapper.delete:
delete
from message
where cu_id = #{cuID}
  and ru_id = #{ruID}

mapper.select:
select *
from message


        cu_id =
        #{cuID}


        and ru_id =
        #{ruID}


        and status =
        #{status}

            or status = 1
